INSERT INTO `articles` VALUES (1, 'PHP 逗号分隔字符串 转数组', 'PHP 逗号分隔字符串', 'PHP 逗号分隔字符串', '#  拿到数据后先将符号转为英文逗号\r\n	  $sUserId = sg($_POST[\'userids\'], 0);\r\n	  $sUserId = preg_replace(\"/(\\n)|(\\s)|(\\t)|(\\\')|(\')|(，)/\" ,\',\' ,$sUserId);\r\n	  $aUserId = explode(\',\', $sUserId); //转换为数组了\r\n# 中文逗号替换为英文\r\n		preg_replace(\"/(，)/\" ,\',\' ,$str);\r\n				或者\r\n		$comment_content =  str_replace(\",\",\"，\",$comment_content);\r\n		\r\n# 把数组转成带单引号的字符串\r\n\r\n* 		比如：array(\"123\",\"321\",\"111\")  转成  \" \'123\',\'321\',\'111\' \" \r\n\r\n````\r\n$a = array(\"123\",\"321\",\"111\");\r\n$s = \"\'\" . join(\"\',\'\", $a) . \"\'\";\r\necho $s;\r\n\r\n````\r\n\r\n\r\n\r\n如果是数字\r\n````\r\n$statusInStr = implode(\',\', $status);\r\n````\r\n字符串\r\n````\r\n$statusInStr = \"\'\".implode(\"\',\'\", $status).\"\'\";\r\n\"select * from table where status in ({$statusInStr})\"\r\n````', 2, 2, 0, 166, 0, 0, '2019-05-22 03:14:55', '2020-06-10 05:53:14', '<h1>拿到数据后先将符号转为英文逗号</h1>\n<pre><code>  $sUserId = sg($_POST[\'userids\'], 0);\n  $sUserId = preg_replace(\"/(\\n)|(\\s)|(\\t)|(\\\')|(\')|(，)/\" ,\',\' ,$sUserId);\n  $aUserId = explode(\',\', $sUserId); //转换为数组了</code></pre>\n<h1>中文逗号替换为英文</h1>\n<pre><code>    preg_replace(\"/(，)/\" ,\',\' ,$str);\n            或者\n    $comment_content =  str_replace(\",\",\"，\",$comment_content);</code></pre>\n<h1>把数组转成带单引号的字符串</h1>\n<ul>\n<li>比如：array(&quot;123&quot;,&quot;321&quot;,&quot;111&quot;)  转成  &quot; \'123\',\'321\',\'111\' &quot; </li>\n</ul>\n<pre><code>$a = array(\"123\",\"321\",\"111\");\n$s = \"\'\" . join(\"\',\'\", $a) . \"\'\";\necho $s;\n</code></pre>\n<p>如果是数字</p>\n<pre><code>$statusInStr = implode(\',\', $status);</code></pre>\n<p>字符串</p>\n<pre><code>$statusInStr = \"\'\".implode(\"\',\'\", $status).\"\'\";\n\"select * from table where status in ({$statusInStr})\"</code></pre>');
INSERT INTO `articles` VALUES (2, '百度ueditor使用', '百度ueditor', '百度ueditor', '```python\r\n<iframe frameborder=\"0\" width=\"100%\" height=\"300\" src=\"真实视频路径\" allowfullscreen=\"allowfullscreen\"></iframe>\r\n```\r\n````\r\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"ueditor/ueditor.config.js\"></script>\r\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"ueditor/ueditor.all.min.js\"> </script>\r\n<script type=\"text/javascript\" charset=\"utf-8\" src=\"ueditor/lang/zh-cn/zh-cn.js\"></script>\r\n \r\n//内容容器\r\n<script id=\"content\" type=\"text/plain\" name=\"content\"></script>\r\n \r\n<script type=\"text/javascript\">\r\n    var ue = UE.getEditor(\'content\', {\r\n        toolbars: [\r\n            [\r\n        //自定义的工具栏，自定义增减 \r\n                \'undo\', //撤销\r\n                \'redo\', //重做\r\n                \'bold\', //加粗\r\n                \'indent\', //首行缩进\r\n                \'italic\', //斜体\r\n                \'underline\', //下划线\r\n                \'strikethrough\', //删除线\r\n                \'subscript\', //下标\r\n                \'fontborder\', //字符边框\r\n                \'superscript\', //上标\r\n                \'formatmatch\', //格式刷\r\n                \'pasteplain\', //纯文本粘贴模式\r\n                \'selectall\', //全选\r\n                \'preview\', //预览\r\n                \'horizontal\', //分隔线\r\n                \'removeformat\', //清除格式\r\n                \'time\', //时间\r\n                \'date\', //日期\r\n                \'inserttitle\', //插入标题\r\n                \'cleardoc\', //清空文档\r\n                \'insertcode\', //代码语言\r\n                \'fontfamily\', //字体\r\n                \'fontsize\', //字号\r\n                \'paragraph\', //段落格式\r\n                \'link\', //超链接\r\n                \'emotion\', //表情\r\n                \'spechars\', //特殊字符\r\n                \'searchreplace\', //查询替换\r\n                \'justifyleft\', //居左对齐\r\n                \'justifyright\', //居右对齐\r\n                \'justifycenter\', //居中对齐\r\n     