  \'strikethrough\', //删除线\n                \'subscript\', //下标\n                \'fontborder\', //字符边框\n                \'superscript\', //上标\n                \'formatmatch\', //格式刷\n                \'pasteplain\', //纯文本粘贴模式\n                \'selectall\', //全选\n                \'preview\', //预览\n                \'horizontal\', //分隔线\n                \'removeformat\', //清除格式\n                \'time\', //时间\n                \'date\', //日期\n                \'inserttitle\', //插入标题\n                \'cleardoc\', //清空文档\n                \'insertcode\', //代码语言\n                \'fontfamily\', //字体\n                \'fontsize\', //字号\n                \'paragraph\', //段落格式\n                \'link\', //超链接\n                \'emotion\', //表情\n                \'spechars\', //特殊字符\n                \'searchreplace\', //查询替换\n                \'justifyleft\', //居左对齐\n                \'justifyright\', //居右对齐\n                \'justifycenter\', //居中对齐\n                \'justifyjustify\', //两端对齐\n                \'forecolor\', //字体颜色\n                \'backcolor\', //背景色\n                \'insertorderedlist\', //有序列表\n                \'insertunorderedlist\', //无序列表\n                \'fullscreen\', //全屏\n                \'directionalityltr\', //从左向右输入\n                \'directionalityrtl\', //从右向左输入\n                \'rowspacingtop\', //段前距\n                \'rowspacingbottom\', //段后距\n                \'imagenone\', //默认\n                \'imageleft\', //左浮动\n                \'imageright\', //右浮动\n                \'imagecenter\', //居中\n                \'lineheight\', //行间距\n                \'edittip \', //编辑提示\n                \'customstyle\', //自定义标题\n                \'autotypeset\', //自动排版\n                \'drafts\', // 从草稿箱加载\n            ]\n        ],\n        autoHeightEnabled: true,\n        autoFloatEnabled: true\n    });\n&lt;/script&gt;</code></pre>');
