INSERT INTO `articles` VALUES (23, 'linux 常用命令', 'linux 常用命令', 'linux 常用命令', '````\r\n查找目录：find /（查找范围） -name \'查找关键字\' -type d\r\n查找文件：find /（查找范围） -name 查找关键字 -print\r\n````', 2, 4, 0, 7, 0, 0, '2019-11-06 04:00:58', '2020-04-02 07:17:32', '<pre><code>查找目录：find /（查找范围） -name \'查找关键字\' -type d\n查找文件：find /（查找范围） -name 查找关键字 -print</code></pre>');
INSERT INTO `articles` VALUES (24, 'jquery 遍历', 'jquery 遍历', 'jquery 遍历', '````\r\nvar cities = [\"1\",\"2\",\"3\",\"4\",\"5\"];\r\n		cities.each(function(i,n) {\r\n		//i 是 value  n 是key\r\n			console.log(i+\" = \"+n);\r\n		});\r\n\r\n````', 2, 3, 0, 7, 0, 0, '2019-11-22 09:37:38', '2020-03-30 11:32:34', '<pre><code>var cities = [\"1\",\"2\",\"3\",\"4\",\"5\"];\n        cities.each(function(i,n) {\n        //i 是 value  n 是key\n            console.log(i+\" = \"+n);\n        });\n</code></pre>');
INSERT INTO `articles` VALUES (25, 'jquery input 的多种操作', 'jquery input 的多种操作', 'jquery input 的多种操作', '#### jq 禁用input\r\n````\r\nobj.attr(\"disabled\", \"disabled\");\r\nobj.removeAttr(\"disabled\");\r\n````\r\n\r\n#### jq input radio 的 值\r\n\r\n````\r\n$(\"input[name=\'is_draw\']:checked\").val()\r\n````\r\n\r\n#### jq 判断复选框是否选中\r\n\r\n````\r\nobj.is(\':checked\')；\r\n````\r\n\r\n#### jq 数组的操作\r\n\r\n````\r\nvar q_7_array = [];\r\nq_7_array.push(‘a’);\r\nq_7_array.splice($.inArray(‘a’,q_7_array),1);\r\n\r\n````', 2, 3, 0, 11, 0, 0, '2019-11-22 09:41:36', '2020-06-16 09:44:24', '<h4>jq 禁用input</h4>\n<pre><code>obj.attr(\"disabled\", \"disabled\");\nobj.removeAttr(\"disabled\");</code></pre>\n<h4>jq input radio 的 值</h4>\n<pre><code>$(\"input[name=\'is_draw\']:checked\").val()</code></pre>\n<h4>jq 判断复选框是否选中</h4>\n<pre><code>obj.is(\':checked\')；</code></pre>\n<h4>jq 数组的操作</h4>\n<pre><code>var q_7_array = [];\nq_7_array.push(‘a’);\nq_7_array.splice($.inArray(‘a’,q_7_array),1);\n</code></pre>');
INSERT INTO `articles` VALUES (26, 'jq 获取字符中数字部分', 'jq 获取字符中数字部分', 'jq 获取字符中数字部分', '````\r\nparseInt(id.replace(/[^0-9]/ig,\"\")\r\n````', 2, 3, 0, 8, 0, 0, '2019-11-22 09:47:49', '2020-04-28 09:10:38', '<pre><code>parseInt(id.replace(/[^0-9]/ig,\"\")</code></pre>');
