INSERT INTO `articles` VALUES (29, '获取某个日期n天之后的日期', '获取某个日期n天之后的日期', '获取某个日期n天之后的日期', '````\r\n<?php\r\n$date=date_create(\"2013-03-15\");\r\ndate_add($date,date_interval_create_from_date_string(\"40 days\"));\r\necho date_format($date,\"Y-m-d\");\r\n?>\r\n````\r\n输出 \r\n\r\n2013-04-24', 5, 2, 0, 1, 0, 0, '2020-05-13 09:22:06', '2020-05-13 09:22:14', '<pre><code>&lt;?php\n$date=date_create(\"2013-03-15\");\ndate_add($date,date_interval_create_from_date_string(\"40 days\"));\necho date_format($date,\"Y-m-d\");\n?&gt;</code></pre>\n<p>输出 </p>\n<p>2013-04-24</p>');
INSERT INTO `articles` VALUES (30, 'vagrant homestead 命令', 'vagrant homestead 命令', 'vagrant homestead 命令', '````\r\n	vagrant ssh\r\n	\r\n	vagrant reload --provision\r\n	\r\n	vagrant box list\r\n	\r\n	update-alternatives --display php 查看所有 php 版本和当前版本\r\n	\r\n  update-alternatives --config php 执行后，会列出当前 php 所有版本和编号，输入编号，切换到执行的版本\r\n\r\n````', 5, 4, 0, 12, 0, 0, '2020-05-19 03:48:52', '2020-06-17 05:56:45', '<pre><code>    vagrant ssh\n\n    vagrant reload --provision\n\n    vagrant box list\n\n    update-alternatives --display php 查看所有 php 版本和当前版本\n\n  update-alternatives --config php 执行后，会列出当前 php 所有版本和编号，输入编号，切换到执行的版本\n</code></pre>');
INSERT INTO `articles` VALUES (31, 'js 判断字符串中是否含有 字符串', 'js 判断字符串中是否含有 字符串', 'js 判断字符串中是否含有 字符串', '````\r\nvar mobile = \'123456\';\r\nif（mobile.indexOf(\'***\') < 0）{\r\n	// 不含有\r\n}else{\r\n	//含有\r\n}\r\n````', 5, 3, 0, 2, 0, 0, '2020-06-04 08:01:36', '2020-06-06 03:27:53', '<pre><code>var mobile = \'123456\';\nif（mobile.indexOf(\'***\') &lt; 0）{\n    // 不含有\n}else{\n    //含有\n}</code></pre>');
INSERT INTO `articles` VALUES (32, 'php csv 导入', 'php csv 导入', 'php csv 导入', '````\r\n\r\n$sFileName = $_FILES[\'user_file\'][\'tmp_name\'];\r\n$oFile = fopen($sFileName, \'r\');\r\n$iRow = 0;\r\nwhile (($aElem = fgetcsv($oFile)) !== false) {\r\n		echo $aElem；\r\n}\r\n\r\nfclose($oFile);\r\n````', 5, 2, 0, 1, 0, 0, '2020-06-06 03:40:00', '2020-06-06 03:40:16', '<pre><code>\n$sFileName = $_FILES[\'user_file\'][\'tmp_name\'];\n$oFile = fopen($sFileName, \'r\');\n$iRow = 0;\nwhile (($aElem = fgetcsv($oFile)) !== false) {\n        echo $aElem；\n}\n\nfclose($oFile);</code></pre>');
INSERT INTO `articles` VALUES (33, 'laravel homestead 自定义 ip地址', 'laravel homestead 自定义 ip地址', 'laravel homestead 自定义 ip地址', '1. 修改 Homestead.yaml 中ip为你的自定义ip\r\n\r\n		\r\n			ip: \"192.168.168.214\"\r\n\r\n\r\n2.  homestead\\scripts\\Homestead.rb 文件 搜索 Network ，修改代码为 \r\n    注意：private_network 改为 public_network，192.168.10.10修改为你的ip地址，注意不要ip冲突\r\n\r\n\r\n			if settings[\'ip\'] != \'autonetwork\'\r\n				config.vm.network :private_network, ip: settings[\'ip\'] ||= \'192.168.188.214\'\r\n			else\r\n				config.vm.network :public_network, ip: \'0.0.0.0\', auto_network: true\r\n			end\r\n\r\n3.  vagrant reload --provision 重载后 如果有问题，请halt后重试', 5, 1, 0, 5, 0, 0, '2020-06-16 09:42:49', '2020-06-17 07:47:48', '<ol>\n<li>\n<p>修改 Homestead.yaml 中ip为你的自定义ip</p>\n<pre><code>    ip: \"192.168.168.214\"</code></pre>\n</li>\n<li>\n<p>homestead\\scripts\\Homestead.rb 文件 搜索 Network ，修改代码为\n注意：private_network 改为 public_network，192.168.10.10修改为你的ip地址，注意不要ip冲突</p>\n<pre><code>    if settings[\'ip\'] != \'autonetwork\'\n        config.vm.network :private_network, ip: settings[\'ip\'] ||= \'192.168.188.214\'\n    else\n        config.vm.network :public_network, ip: \'0.0.0.0\', auto_network: true\n    end</code></pre>\n</li>\n<li>\n<p>vagrant reload --provision 重载后 如果有问题，请halt后重试</p>\n</li>\n</ol>');

